import {React, useEffect, useState} from 'react';
import {Line, Doughnut, Bar, HorizontalBar} from "react-chartjs-2"

function Graph() {
    const [data, setData] = useState({});
    const [lData, setLdata] = useState({
        labels: [],
        datasets:[
            {
                label:'',
                data:[],
                backgroundColor:[]
            }
        ]
    });
    useEffect(() => {
        fetch("https://disease.sh/v3/covid-19/historical/all?lastdays=100")
        .then(res => res.json())
        .then(data => {
            const diff = (cases)=>{
                var dataArr = Object.values(data.cases);
                var newArr = [];
                for(var i=0; i<dataArr.length-1;i++)
                {
                    var difference = dataArr[i+1] - dataArr[i];
                    newArr.push(difference);

                }

                console.log(newArr);
                return newArr;

            }

            const colors = (num) => {
                var colorsArr = [];
                for(var i=0; i<Object.values(num).length-1; i++)
                {
                    colorsArr.push('rgba(255, 99, 132, 0.2)');
                }
                 return colorsArr;
            }

            diff(data.cases);

            setLdata({
                labels:Object.keys(data.cases),
                datasets:[
                    {
                        label:'Corona cases',
                        data: diff(data.cases),
                        backgroundColor:colors(data.cases)
                    }
                ]

            });


        })
    }, [])
    return (
        <div>

            <Bar data={lData} />

        </div>
    )
}

export default Graph;
